DROP TABLE IF EXISTS Artwork;

CREATE TABLE IF NOT EXISTS Artwork(
  `id` mediumint(8) unsigned NOT NULL auto_increment,
  `title`        VARCHAR(61) NOT NULL,
  `artist`       VARCHAR(30) NOT NULL,
  `year`         INTEGER,
  `medium`       VARCHAR(70),
  `dimensions`   VARCHAR(79),
  `url`          VARCHAR(109),
  `initialPrice` INTEGER,
  `sold`         BOOLEAN,
  `location`     mediumint(8) unsigned NOT NULL,
  `seller`       mediumint(8) unsigned NOT NULL,
  PRIMARY KEY (`id`),
  FOREIGN KEY (`location`) REFERENCES AuctionHouse(`id`),
  FOREIGN KEY (`seller`) REFERENCES Seller(`id`)
) AUTO_INCREMENT=1;

